// src/pages/Home.js
import React, { useEffect, useState } from 'react';
import './Home.css';
import getProducts from '../services/productData'; 
const Home = () => {
  const [products, setProducts] = useState([]);

  // Récupérer les produits depuis l'API (ou fichier)
  useEffect(() => {
    const fetchProducts = async () => {
      const data = await getProducts();
      setProducts(data);
    };
    fetchProducts();
  }, []);
  
  // Génération automatique de 50 produits
  const generatedProducts = Array.from({ length: 50 }, (_, i) => ({
    id: i + 1,
    name: `Produit ${i + 1}`,
    price: Math.floor(Math.random() * 500) + 10,
    description: `Description du produit ${i + 1}`,
    image: `https://picsum.photos/seed/${i + 1}/220/220`
  }));
  
  return (
    <div className="container">
      <h1 style={{ textAlign: 'center', marginBottom: 30 }}>Bienvenue sur notre boutique !</h1>
      <div className="product-list" style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(220px, 1fr))', gap: 20 }}>
        {generatedProducts.map(product => (
          <div className="product-card" key={product.id} style={{ boxShadow: '0 2px 8px #eee', borderRadius: 10, padding: 15, background: '#fff', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <img src={product.image} alt={product.name} style={{ width: '100%', borderRadius: 8, marginBottom: 10 }} />
            <h2 style={{ margin: '10px 0 5px' }}>{product.name}</h2>
            <p style={{ color: '#555', marginBottom: 10 }}>{product.description}</p>
            <p style={{ fontWeight: 'bold', fontSize: '1.1rem', marginBottom: 10 }}>{product.price} €</p>
            <button style={{ background: '#ff6600', color: '#fff', border: 'none', padding: '8px 16px', borderRadius: 5, cursor: 'pointer' }}>
              Voir le produit
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
